import React from 'react';


class Api extends React.Component {
	constructor() {
		super()
		this.state = {
			user : []
		}
	}

	componentDidMount() {
		let url = 'https://swapi.co/api/people/1';

		fetch(url)
			.then(response =>  response.json())
			.then(data => {
				this.setState = {
					user : data
				}
			});


	}

	render() {
		return (
			<div>
				<h1>Api Example</h1>
				{
					this.state.user.name
				}
			</div>
		)
	}
}

export default Api;
