import React from 'react';
import RegisterForm from '../Froms/Register';

class Register extends React.Component {

	constructor() {
		super()

		this.state = {
			name : '',
			email : '',
			password : '',
			isChecked : false,
			gender : '',
			position : '',
			knowledge : {
				php : false,
				java : false,
				javascript : false,
			}
		}

		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleSubmit() {
		console.log('Hello');
	}

	handleChange(event) {

		const target = event.target;
		const value = (target.type === 'checkbox') ? target.checked : target.value;
		const name = target.name;

		if (name === 'php' || name === 'java' || name === 'javascript') {


			// if you don't know spread syntax
			let knowledge = Object.assign({} , this.state.knowledge);
			knowledge[name] = target.checked
			this.setState({knowledge});

			/*
			With spread 
			this.setState(prevState => ({
				knowledge : {
					...prevState.knowledge,
					[name] : target.checked
				}
			}))*/
		} else {
			this.setState({
				[name] : value
			})
		}


		// without spread operator

		// let knowledge = Object.assign({} , this.state.knowledge);

		/*
		value = (event.target.type === 'radio' ) ? event.target.checked : event.target.value;
		const {name, value} = event.target;
		this.setState({
			[event.target.name] : event.target.value
		})*/

		/*
		Basic Implementation From Here
		this.setState({
			name : event.target.value
		})*/
	}

	render() {
		return (
			<div className='container'>
				<div className='col-md-offset-3 col-md-6'>
					<h3>User Registration</h3>
					<RegisterForm/>
				</div>
			</div>
		)
	}
}

export default Register
