class MainContent  extends React.Component {

	constructor() {
		super()

		state = {
			todos : this.props.todoItems
		}


		this.handleChange = this.handleChange.bind(this)
	}

	handleChange(id) {
		this.setState(prevSate => {
			const updatedTodos = prevState.todos.map(todo => {
				if (todo.id === id) {
				  todo.completed = !todo.completed
				}
				return todo
			})
			return {
				todos: updatedTodos
			}
		})		

		/*this.props.todoItems.map(prevTodo => {

			console.log(prevTodo);
			const updatedTodos = this.props.todoItems.map(todo => { 
				if (todo.id === id) {
					todo.completed = !todo.completed
				}
				return todo;
			})

			return {
				todo : updatedTodos 
			};
		})*/
	}

	render() {
		return (
			<main className='container'>
				<ul className='list-group'>
					{
						this.props.todoItems.map((todo,key) => <TodoItems key={key} handleChange={this.handleChange} todo={todo}/>)
					}
				</ul>
			</main>
		)
	}
}



export default MainContent;
