import React from 'react';


class FunctionalMainContent extends React.Component {

	constructor() {
		super();

		console.log(this.state);
	}

	handleChange() {
		console.log("Changed the checkbox state");
	}

	render() {
		return (
			<main className='container'>
				<ul className='list-group'>
				{
					this.state.todo.todoItems.map((todo, key) =>
						<li key={todo.id} className='list-group-item form-group'>
							<div className='checkbox'>
								<input type='checkbox' onChange={this.handleChange} name='check'  checked={todo.completed} />
								<label className=''> {todo.title} </label>
							</div>
						</li>
					)
				}
				</ul>
			</main>
		)	
	}	
}
const MainContent = (props) => {

	return (
		<main className='container'>
			<ul className='list-group'>
			{
				props.todoItems.map((todo, key) =>
					<li key={todo.id} className='list-group-item form-group'>
						<div className='checkbox'>
							<input type='checkbox' name='check'  checked={todo.completed} />
							<label className=''> {todo.title} </label>
						</div>
					</li>
				)
			}
			</ul>
		</main>
	)	
}


export default MainContent;
