import React from 'react'

class Register extends React.Component {

	constructor() {
		super()

		this.state = {
			'name' : '',
			'email' : '',
			'password' : '',
			'gender' : false,
		}

		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleSubmit() {
		console.log('Hello');
	}

	handleChange(event) {

		const {name, value} = event.target;
		value = event.target.name === 'radio' || 'checkbox' ? event.target.checked : event.target.value;
		this.setState({
			[name] : value
		})

		/*this.setState({
			[event.target.name] : event.target.value
		})*/

		/*
		Basic Implementation From Here
		this.setState({
			name : event.target.value
		})*/
	}

	render() {
		return (
			<div className='container'>
				<div className='col-md-offset-3 col-md-6'>
					<h3>User Registration</h3>
					<form onSubmit={this.handleSubmit}>
						<div className='form-group'>
							<label>Name {this.state.name}</label>
							<input type='text' name='name' placeholder='Name' className='form-control' onChange={this.handleChange} />
							{ this.state.name }
						</div>
						<div className='form-group'>
							<label>Email {this.state.email}</label>
							<input type='text' name='email' placeholder='Email' className='form-control' onChange={this.handleChange} />
						</div>
						<div className='form-group'>
							<label>Password {this.state.password}</label>
							<input type='password' name='password' placeholder='Password' className='form-control' onChange={this.handleChange} />
						</div>
						<div className='form-group'>
							<label className='checkbox'><input type='radio' onChange={this.handleChange} name='gender' value={this.state.gender}/> Male</label>
							<label className='checkbox'><input type='radio' onChange={this.handleChange} name='gender' value={this.state.gender}/> Female</label>

							{this.state.gender}
						</div>
						<div className='form-group'>
							<input type='submit' className='btn btn-primary' />
						</div>
					</form>
				</div>
			</div>
		)
	}
}

export default Register
