import React from 'react'

class Register extends React.Component {

	constructor() {
		super()

		this.state = {
			name : '',
			email : '',
			password : '',
			isChecked : false,
			gender : '',
			position : '',
			knowledge : {
				php : false,
				java : false,
				javascript : false,
			}
		}

		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleSubmit() {
		console.log('Hello');
	}

	handleChange(event) {

		const target = event.target;
		const value = (target.type === 'checkbox') ? target.checked : target.value;
		const name = target.name;

		if (name === 'php' || name === 'java' || name === 'javascript') {


			// if you don't know spread syntax
			let knowledge = Object.assign({} , this.state.knowledge);

			this.setState(
				knowledge[name] = target.checked
			);

			/*
			this.setState(prevState => ({
				knowledge : {
					...prevState.knowledge,
					[name] : target.checked
				}
			}))*/
		} else {
			this.setState({
				[name] : value
			})
		}


		// without spread operator

		// let knowledge = Object.assign({} , this.state.knowledge);

		/*
		value = (event.target.type === 'radio' ) ? event.target.checked : event.target.value;
		const {name, value} = event.target;
		this.setState({
			[event.target.name] : event.target.value
		})*/

		/*
		Basic Implementation From Here
		this.setState({
			name : event.target.value
		})*/
	}

	render() {
		return (
			<div className='container'>
				<div className='col-md-offset-3 col-md-6'>
					<h3>User Registration</h3>
					<form onSubmit={this.handleSubmit}>
						<div className='form-group'>
							<label>Name {this.state.name}</label>
							<input type='text' name='name' placeholder='Name' className='form-control' onChange={this.handleChange} />
							{ this.state.name }
						</div>
						<div className='form-group'>
							<label>Email {this.state.email}</label>
							<input type='text' name='email' placeholder='Email' className='form-control' onChange={this.handleChange} />
						</div>
						<div className='form-group'>
							<label>Password {this.state.password}</label>
							<input type='password' name='password' placeholder='Password' className='form-control' onChange={this.handleChange} />
						</div>

						<div className='form-group'>
							<label>Position {this.state.position}</label>
							<select value={this.state.position} className='form-control' name='position' onChange={this.handleChange}>
								<option value=''>Select Position</option>
								<option value='software-developer'>Software Developer</option>
								<option value='carpainter'>Carpainter</option>
								<option value='barber'>Barber</option>
							</select>
						</div>

						<div className='form-group'>
							<label>Gender</label>
							<label className='checkbox'><input type='radio' checked={this.state.gender === 'Male'} onChange={this.handleChange} name='gender'  value='Male'/> Male</label>
							<label className='checkbox'><input type='radio' checked={this.state.gender === 'Female'} onChange={this.handleChange} name='gender'  value='Female'/> Female</label>

							{this.state.gender}
						</div>

						<div className='form-group'>
							<label>Knowledge</label>
							<div className=''>

								<label className='checkbox'><input type='checkbox' onChange={this.handleChange} name='php' checked={this.state.knowledge.php}/> PHP</label>
								<label className='checkbox'><input type='checkbox' onChange={this.handleChange} name='java' checked={this.state.knowledge.java}/> Java</label>
								<label className='checkbox'><input type='checkbox' onChange={this.handleChange} name='javascript' checked={this.state.knowledge.javascript}/> Javascript</label>
							</div>
						</div>


						<div className='form-group'>
							<input type='submit' className='btn btn-primary' />
						</div>
					</form>
				</div>
			</div>
		)
	}
}

export default Register
